const { createLogger, format, transports } = require('winston');
require('dotenv').config({ path: '.env-auth-service' });

const logger = createLogger({
    level: 'info',
    format: format.combine(
        format.timestamp(),
        format.errors({ stack: true }),
        format.splat(),
        format.json()
    ),
    defaultMeta: { service: 'pa-auth-service' },
    transports: [
        new transports.File({ filename: `${process.env.SERVICE_NAME_SCHEMA}-info.log`, level: 'info' }),
        new transports.File({ filename: `${process.env.SERVICE_NAME_SCHEMA}-error.log`, level: 'error' }),
        new transports.Console({ filename: `${process.env.SERVICE_NAME_SCHEMA}-combined.log` }),
    ],
});

if (process.env.NODE_ENV !== 'production') {
    logger.add(new transports.Console({
        format: format.combine(
            format.colorize(),
            format.simple()
        )
    }));
}

module.exports = logger;
